		function resetData(checkSport, checkTeam){
			if (loggedIn){
				espn.p13n.reset({
					success: function(data){
						if (data != null && data != 'UNDEF' && typeof(data.myTeams) != 'UNDEF' && data.myTeams != null){
							var count = 0;
							var sportTypes = {10:'mlb',46:'nba',28:'nfl',90:'nhl',23:'ncf',41:'ncb',54:'ncw',600:'soccer'}
							var sportAbbrevs = {10:'MLB',46:'NBA',28:'NFL',90:'NHL',23:'NCAAF',41:'NCAAM',54:'NCAAW',600:'Soccer'}
							$.each(data.myTeams, function(i,item){
								if ((checkTeam == item.id && checkSport == item.sport) || (checkTeam == 0 && checkSport == 0)){ // when updating a team, only update that specific team
									var logo = '';
									if (typeof(item.sport) != 'UNDEF' && item.sport != null){
										sportType = sportTypes[item.sport];
										sportAbbrev = sportAbbrevs[item.sport];
										sport = item.sport;
										abbrev = '';
										swabbrev = '';
										if (typeof(item.abbrev) != 'UNDEF' && item.abbrev != null){
											abbrev = item.abbrev.toLowerCase();
										}
										if (typeof(item.sw_abbrev) != 'UNDEF' && item.sw_abbrev != null){
											swabbrev = item.sw_abbrev.toLowerCase();
										}
										desc = item.text;
										descSEO = desc;
										while(descSEO.indexOf(' ')!=-1)descSEO=descSEO.replace(' ','-');
										if (sport == 10 || sport == 28 || sport == 90 || sport == 46){ // mlb, nfl and nhl (new reshelled clubhouses will go here)
											if (swabbrev == ''){ swabbrev = abbrev; }
											logo = '
';
										}
										else if (sportType == 'ncf'){
											tAbbrev = item.id
											logo = '';
										}
										else if (sportType == 'ncb'){
											tAbbrev = item.id
											logo = '';
										}
										else if (sportType == 'ncw'){ // ncaa
											tAbbrev = item.id;
											logoAbbrev = tAbbrev
											if (item.sport == 54 && item.id == 2633){ logoAbbrev = tAbbrev + '_ncw'; }
											logo = '';
										}
										else if (sportType == 'soccer'){ // all inclusive soccer list
											logo = '';
										}
										if (count == 0 && !displayModule){
											$('.mod-myespn .personalization-teams').html('');
										}
										if (logo != ''){
											if (checkTeam == item.id && checkSport == item.sport){
												$('.mod-myespn .personalization-teams').append(logo).fadeIn(fadeTime, function(){
													espn.core.init.faves();
												});
											}
											else{
												$('.mod-myespn .personalization-teams').append(logo);
											}
										}
										count++;
									}
									if (count == 1 && !displayModule){
										$('#myespn-personalization').show();
										displayModule = true;
									}
								}
							});
						}
					}
